import React from 'react';
import { connect } from 'react-redux';
import { Link, RouteComponentProps } from 'react-router-dom';
import { Button, Col, Row, Table } from 'reactstrap';
// tslint:disable-next-line:no-unused-variable
import {
  openFile,
  byteSize,
  ICrudGetAllAction,
  getSortState,
  IPaginationBaseState,
  getPaginationItemsNumber,
  JhiPagination
} from 'react-jhipster';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

import { IRootState } from 'app/shared/reducers';
import { getEntities } from './complain.reducer';
import { IComplain } from 'app/shared/model/complain.model';
// tslint:disable-next-line:no-unused-variable
import { APP_DATE_FORMAT, APP_LOCAL_DATE_FORMAT } from 'app/config/constants';
import { ITEMS_PER_PAGE } from 'app/shared/util/pagination.constants';

export interface IComplainProps extends StateProps, DispatchProps, RouteComponentProps<{ url: string }> {}

export type IComplainState = IPaginationBaseState;

export class Complain extends React.Component<IComplainProps, IComplainState> {
  state: IComplainState = {
    ...getSortState(this.props.location, ITEMS_PER_PAGE)
  };

  componentDidMount() {
    this.getEntities();
  }

  sort = prop => () => {
    this.setState(
      {
        order: this.state.order === 'asc' ? 'desc' : 'asc',
        sort: prop
      },
      () => this.sortEntities()
    );
  };

  sortEntities() {
    this.getEntities();
    this.props.history.push(`${this.props.location.pathname}?page=${this.state.activePage}&sort=${this.state.sort},${this.state.order}`);
  }

  handlePagination = activePage => this.setState({ activePage }, () => this.sortEntities());

  getEntities = () => {
    const { activePage, itemsPerPage, sort, order } = this.state;
    this.props.getEntities(activePage - 1, itemsPerPage, `${sort},${order}`);
  };

  render() {
    const { complainList, match, totalItems } = this.props;
    return (
      <div>
        <h2 id="complain-heading">
          Complains
          <Link to={`${match.url}/new`} className="btn btn-primary float-right jh-create-entity" id="jh-create-entity">
            <FontAwesomeIcon icon="plus" />&nbsp; Create new Complain
          </Link>
        </h2>
        <div className="table-responsive">
          <Table responsive>
            <thead>
              <tr>
                <th className="hand" onClick={this.sort('id')}>
                  ID <FontAwesomeIcon icon="sort" />
                </th>
                <th className="hand" onClick={this.sort('details')}>
                  Details <FontAwesomeIcon icon="sort" />
                </th>
                <th className="hand" onClick={this.sort('img')}>
                  Img <FontAwesomeIcon icon="sort" />
                </th>
                <th className="hand" onClick={this.sort('expection')}>
                  Expection <FontAwesomeIcon icon="sort" />
                </th>
                <th className="hand" onClick={this.sort('showAnonymous')}>
                  Show Anonymous <FontAwesomeIcon icon="sort" />
                </th>
                <th className="hand" onClick={this.sort('escalate')}>
                  Escalate <FontAwesomeIcon icon="sort" />
                </th>
                <th className="hand" onClick={this.sort('resolution')}>
                  Resolution <FontAwesomeIcon icon="sort" />
                </th>
                <th>
                  Department <FontAwesomeIcon icon="sort" />
                </th>
                <th>
                  Status <FontAwesomeIcon icon="sort" />
                </th>
                <th />
              </tr>
            </thead>
            <tbody>
              {complainList.map((complain, i) => (
                <tr key={`entity-${i}`}>
                  <td>
                    <Button tag={Link} to={`${match.url}/${complain.id}`} color="link" size="sm">
                      {complain.id}
                    </Button>
                  </td>
                  <td>{complain.details}</td>
                  <td>
                    {complain.img ? (
                      <div>
                        <a onClick={openFile(complain.imgContentType, complain.img)}>
                          <img src={`data:${complain.imgContentType};base64,${complain.img}`} style={{ maxHeight: '30px' }} />
                          &nbsp;
                        </a>
                        <span>
                          {complain.imgContentType}, {byteSize(complain.img)}
                        </span>
                      </div>
                    ) : null}
                  </td>
                  <td>{complain.expection}</td>
                  <td>{complain.showAnonymous ? 'true' : 'false'}</td>
                  <td>{complain.escalate}</td>
                  <td>{complain.resolution}</td>
                  <td>{complain.department ? <Link to={`department/${complain.department.id}`}>{complain.department.id}</Link> : ''}</td>
                  <td>{complain.status ? <Link to={`status/${complain.status.id}`}>{complain.status.id}</Link> : ''}</td>
                  <td className="text-right">
                    <div className="btn-group flex-btn-group-container">
                      <Button tag={Link} to={`${match.url}/${complain.id}`} color="info" size="sm">
                        <FontAwesomeIcon icon="eye" /> <span className="d-none d-md-inline">View</span>
                      </Button>
                      <Button tag={Link} to={`${match.url}/${complain.id}/edit`} color="primary" size="sm">
                        <FontAwesomeIcon icon="pencil-alt" /> <span className="d-none d-md-inline">Edit</span>
                      </Button>
                      <Button tag={Link} to={`${match.url}/${complain.id}/delete`} color="danger" size="sm">
                        <FontAwesomeIcon icon="trash" /> <span className="d-none d-md-inline">Delete</span>
                      </Button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
        <Row className="justify-content-center">
          <JhiPagination
            items={getPaginationItemsNumber(totalItems, this.state.itemsPerPage)}
            activePage={this.state.activePage}
            onSelect={this.handlePagination}
            maxButtons={5}
          />
        </Row>
      </div>
    );
  }
}

const mapStateToProps = ({ complain }: IRootState) => ({
  complainList: complain.entities,
  totalItems: complain.totalItems
});

const mapDispatchToProps = {
  getEntities
};

type StateProps = ReturnType<typeof mapStateToProps>;
type DispatchProps = typeof mapDispatchToProps;

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Complain);
